SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
START TRANSACTION;
SET time_zone = "+00:00";

USE `dbfichasocioeconomica`;

DROP TABLE IF EXISTS `provincias`;
CREATE TABLE IF NOT EXISTS `provincias` (
	`id`                    int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
	`codigo`                varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
	`descripcion`           varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
	`codigodeptamento`   varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
	`codigoprovincia`      varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL,
	`departamento_id`       int(10) UNSIGNED NOT NULL,

	`activo`      			tinyint(1) NOT NULL DEFAULT '1',
	`created_at`  			timestamp NULL DEFAULT CURRENT_TIMESTAMP,
	`updated_at`  			timestamp NULL DEFAULT NULL,
	PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=196 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



COMMIT;
